import React from "react";

class Form extends React.Component{
    constructor(props){
        super(props);
        //to use setState is necessary to use bind

        this.handleChange = this.handleChange.bind(this);

        this.state = {
            name: "",
            email:""
        };
    }

    handleChange(e){
        this.setState({
            name: e.target.value
        });
    }

    handleChangeEmail(e){
        this.setState({
            email: e.target.value
        })
    }

    render(){
        return (
            <>
                <hr/>
                <section>
                    <h1>Form</h1>
                    <h2>Sup {this.state.name} are your e-mail {this.state.email} ?</h2>
                    <div>
                        <form onSubmit={this.handleSubmit}>
                            <label name="name">Name:</label>
                            <br/>
                            <input type="text" name="name" id="" onChange={this.handleChange}/>
                            <br/>
                            <label name="email">
                                E-mail:
                            </label>
                            <br/>
                            <input type="email" name="email" onChange={this.handleChangeEmail}/>
                            <br/>
                            <br/>
                            <button type="submit">
                                Send
                            </button>
                        </form>
                    </div>
                </section>
                <hr/>
            </>
        );
    }
}

export default Form;
