import React from "react";

class Events extends React.Component{

    constructor(props){
        //heritage of React.Component to use state and others unknowed properties

        super(props);
        this.state = {
            clicked: true
        }

        //that way we can use all the class methods inside the function
        this.eventFuction = this.eventFunction.bind(this);
    }

    eventFunction(e,state=this.state){
        e.preventDefault();
        alert(this.state);
    }
    render(){
        return (<>
            <div style={{color: "orange"}}>
                <div style={{color:this.state.clicked ? "green" : "red"}}>
                    {this.state.clicked ? "ON" : "OFF"}
                </div>
                <a href="#" onClick={this.eventFunction}>
                    Click here!
                </a>
            </div>
        </>);
        }
    }



export default Events;
