import React from "react";

class Form extends React.Component{
    constructor(props){
        super(props);
        //to use setState is necessary to use bind

        this.handleChange = this.handleChange.bind(this);

        this.state = {
            name: "",

        };
    }

    handleChange(e){
        this.setState({
            name: e.target.value
        })
    }

    render(){
        return (
            <>
                <hr/>
                <section>
                    <h1>Form</h1>
                    <h2>Sup {this.state.name}</h2>
                    <div>
                        <form>
                            <label >Name:</label>
                            <input type="text" name="name" id="" onChange={this.handleChange}/>
                        </form>
                    </div>
                </section>
                <hr/>
            </>
        );
    }
}

export default Form;
