import React from "react";

class Events extends React.Component{

    constructor(props){
        //heritage of React.Component to use state and others unknowed properties

        super(props);
        this.state = {
            bool: "lol"
        }
    }

    componentDidMount(){
        setTimeout(()=>{
            this.state = {bool:"LOL"}
        },100);
        setTimeout(()=>{
            this.state = {bool:"LoL"}
        },3000)

    }

    render(){
        return (<>
            <div style={{color: "orange"}}>
                <h1>{this.state.bool}</h1>
            </div>
        </>);
    }
}

export default Events;
