class Events extends React.Component{

    constructor(props){
        //heritage of React.Component to use state and others unknowed properties

        super(props);
        this.state = {
            clicked: false,
            status: "ON",
            color: "green"
        }
    }

    eventFuction(e){
        e.preventDefault();
        if(this.state.clicked){
            this.setState(
                state=>({
                    clicked: false,
                    status: "OFF",
                    color:"red"
                })
            );
        }else{
            this.setState(
                state=>({
                    clicked: false,
                    status: "ON",
                    color: "green"
                })
            );
        }
    }

    render(){
        return (<>
            <div style={{color: "orange"}}>
