import React from "react";

function Css(props){
    const firstPStyle = {
        color: "green",
        fontFamily: "Arial",
        fontSize: 23,
        padding: "30px",
    };
    return (
        <>
            <div>
                <h1 style={{color:'mediumspringgreen'}}>CSS inline</h1>
                <p style={firstPStyle}>
                    Also we are allowed to define an object containing all the styles
                </p>
            </div>
        </>
    );
}

export default Css;
