import {useState} from "react";

function useForm(){
    const [values, setValues] = useState({});

    function handleChange(e){
        const auxValues = {...values};
        alert(auxValues);
        auxValues[e.target.name] = e.target.value;
        setValues(auxValues)
    }

    return [{values}, handleChange]
}

export default useForm;
